import React, { useState, useEffect } from 'react';
import './App.css';
import Card from './card';
import axios from 'axios';
import logo from './UI-Screen-1.png';

function App() {
  const [items, setItems] = useState([]);
  const [searchInput, setSearchInput] = useState('');
  const [filteredItems, setFilteredItems] = useState([]);

  useEffect(() => {
    axios.get("http://localhost:4000/cards")
      .then((response) => {
        setItems(response.data);
        setFilteredItems(response.data);
      })
      .catch((error) => {
        console.error("Error fetching data:", error);
      });
  }, []);

  const handleSearchChange = (e) => {
    const value = e.target.value;
    setSearchInput(value);

    const temp = items.filter(item =>
      item.title.toLowerCase().includes(value.toLowerCase()) ||
      item.description.toLowerCase().includes(value.toLowerCase())
    );

    setFilteredItems(temp);
  };

  return (
    <div className="App">
      <header className="App-header">
        <nav>
          <div className="logo"><img src={logo} alt="Abstract Logo" /> Abstract | Help Center</div>
          <ul>
            <li><button className="request-button">Submit a request</button></li>
          </ul>
        </nav>
      </header>

      <section className="hero">
        <h1>How can we help?</h1>
        <div className="search-box">
          <input
            type="text"
            placeholder="search"
            value={searchInput}
            onChange={handleSearchChange}
            aria-label="Search items"
          />
          <button onClick={() => {}} className="arrow" aria-label="Search">
            â†’
          </button>
        </div>
      </section>

      <main>
        <div className="content">
          {filteredItems.map((data) => (
            <Card key={data.id} title={data.title} desc={data.description} />
          ))}
        </div>
      </main>

      <footer className="footer">
        {/* Footer content remains unchanged */}
      </footer>
    </div>
  );
}

export default App;
