// src/App.js
import React from 'react';
import './App.css';
import Card from './components/Card';

function App() {
  const items = [
    { title: "Game", desc: "A fun activity to play" },
    { title: "Book", desc: "A source of knowledge" },
    { title: "Movie", desc: "An entertaining film" },
    { title: "Music", desc: "A collection of sounds" }
  ];

  return (
    <div className="App">
      {/* Header */}
      <header className="App-header">
        <nav>
          <div className="logo">Abstract</div>
          <ul>
            <li>Pricing</li>
            <li>Help Center</li>
          </ul>
        </nav>
      </header>

      {/* Hero Section */}
      <section className="hero">
        <h1>How can we help?</h1>
        <input type="text" placeholder="Describe your issue or share your ideas" />
        <button>Submit</button>
      </section>

      {/* Main Content */}
      <main>
        <div className="content">
          {items.map((data, index) => (
            <Card key={index} title={data.title} desc={data.desc} />
          ))}
        </div>
      </main>

      {/* Footer */}
      <footer>
        <div className="footer-section">Abstract</div>
        <div className="footer-section">Resources</div>
        <div className="footer-section">Community</div>
        <div className="footer-section">Company</div>
      </footer>
    </div>
  );
}

export default App;
